CREATE TABLE Patient (ID BIGINT NOT NULL, AGE INTEGER, BIRTHDATE DATE, NAME VARCHAR(255), PATIENTID BIGINT, PRIMARY KEY (ID))
CREATE TABLE Treatment (ID BIGINT NOT NULL, TREATMENT_TYPE VARCHAR(31), DIAGNOSIS VARCHAR(255), TREATMENTTYPE VARCHAR(255), PATIENT_ID BIGINT NOT NULL, PROVIDERS_ID BIGINT NOT NULL, PRIMARY KEY (ID, PATIENT_ID, PROVIDERS_ID))
CREATE TABLE DrugTreatment (ID BIGINT NOT NULL, DOSAGE FLOAT, DRUG VARCHAR(255), DRUGTREATMENTID BIGINT, PRIMARY KEY (ID))
CREATE TABLE Provider (ID BIGINT NOT NULL, NAME VARCHAR(255), NPI BIGINT, PATIENT_ID BIGINT, PRIMARY KEY (ID))
CREATE TABLE Radiology (ID BIGINT NOT NULL, RID BIGINT, PRIMARY KEY (ID))
CREATE TABLE Surgery (ID BIGINT NOT NULL, DIAGNOSIS VARCHAR(255), DATE DATE, SID BIGINT, PRIMARY KEY (ID))
CREATE TABLE Radiology_date (Radiology_RID BIGINT, DATE DATE)
ALTER TABLE Treatment ADD CONSTRAINT FK_Treatment_PATIENT_ID FOREIGN KEY (PATIENT_ID) REFERENCES Patient (ID)
ALTER TABLE Treatment ADD CONSTRAINT FK_Treatment_PROVIDERS_ID FOREIGN KEY (PROVIDERS_ID) REFERENCES Provider (ID)
ALTER TABLE DrugTreatment ADD CONSTRAINT FK_DrugTreatment_ID FOREIGN KEY (ID) REFERENCES Treatment (ID)
ALTER TABLE Provider ADD CONSTRAINT FK_Provider_PATIENT_ID FOREIGN KEY (PATIENT_ID) REFERENCES Patient (ID)
ALTER TABLE Radiology ADD CONSTRAINT FK_Radiology_ID FOREIGN KEY (ID) REFERENCES Treatment (ID)
ALTER TABLE Surgery ADD CONSTRAINT FK_Surgery_ID FOREIGN KEY (ID) REFERENCES Treatment (ID)
ALTER TABLE Radiology_date ADD CONSTRAINT FK_Radiology_date_Radiology_RID FOREIGN KEY (Radiology_RID) REFERENCES Radiology (RID)
CREATE TABLE SEQUENCE (SEQ_NAME VARCHAR(50) NOT NULL, SEQ_COUNT DECIMAL(38), PRIMARY KEY (SEQ_NAME))
INSERT INTO SEQUENCE(SEQ_NAME, SEQ_COUNT) values ('SEQ_GEN', 0)
